import React, { useState } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import './styles.css';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected, addNewRoot }) => {
  const handleActionClick = (e, action) => {
    e.stopPropagation();
    action();
  };

  const selected = isSelected(node);

  return (
    <TreeNode label={
      <div className={`tree-label ${selected ? 'selected' : ''}`} onClick={() => onSelectNode(node)}>
        <div className="name">{node.name}</div>
        <div className="buttons">
          <button className="add-button" onClick={(e) => handleActionClick(e, () => onAddChild(node, true))}>+</button>

          {node.id !== 'root' && (
            <button className="remove-button" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>x</button>
          )}
        </div>
      </div>
    }>
      {node.children && node.children.map(child => (
        <Holon 
          key={child.id} 
          node={child} 
          onAddChild={onAddChild} 
          onRemoveNode={onRemoveNode} 
          onSelectNode={onSelectNode} 
          isSelected={isSelected}
          addNewRoot={addNewRoot}
        />
      ))}
    </TreeNode>
  );
};

const App = () => {
  const initialNodes = { /* Initial nodes structure */ };

  const [nodes, setNodes] = useState(initialNodes);
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState(""); // 'child' or 'parent'
  const [nodeName, setNodeName] = useState("");

  const handleModalClose = () => setShowModal(false);
  const handleModalShow = (type) => {
    setModalType(type);
    setShowModal(true);
  };

  const onAddChild = (parentNode, isRoot = false) => {
    handleModalShow('child');
  };

  const addNewRoot = () => {
    handleModalShow('parent');
  };

  const handleSubmit = () => {
    if (!nodeName) return;

    if (modalType === "child") {
      const newChild = {
        id: `${selectedHolon.id}-${Math.random()}`,
        name: nodeName,
        children: []
      };

      const addNode = (nodes, parentId) => {
        if (nodes.id === parentId) {
          nodes.children.push(newChild);
          return;
        }

        nodes.children.forEach(child => addNode(child, parentId));
      };

      const newNodes = { ...nodes };
      addNode(newNodes, selectedHolon.id);
      setNodes(newNodes);
    } else if (modalType === "parent") {
      const newParent = {
        id: `root-${Math.random()}`,
        name: nodeName,
        children: [selectedHolon]
      };

      setNodes(newParent);
    }

    handleModalClose();
  };

  return (
    <div className="app-container">
      <Tree /* Tree props */>
        <Holon
          node={nodes}
          onAddChild={onAddChild}
          onRemoveNode={onRemoveNode}
          onSelectNode={onSelectNode}
          isSelected={isSelected}
          addNewRoot={addNewRoot}
        />
      </Tree>
      {/* Modal for Node Name Input */}
      <Modal show={showModal} onHide={handleModalClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add New {modalType === 'child' ? 'Child' : 'Parent'} Node</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3" controlId="nodeName">
              <Form.Label>Node Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter node name"
                value={nodeName}
                onChange={(e) => setNodeName(e.target.value)}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleModalClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default App;
