import React, { useState, useCallback } from 'react';
import './App.css';

// Initial system data
const initialSystem = {
  name: 'Information System',
  description: 'Top-level entity of an information system.',
  children: [
    {
      name: 'Stakeholder',
      description: 'Represents an entity with interest in the system.',
      children: [],
    },
    {
      name: 'Infrastructure',
      description: 'Underlying technical structure of the system.',
      children: [],
    },
  ],
};

// CanvasView Component
const CanvasView = ({ system, setSelectedHolon }) => {
  const renderSystem = (holon, level = 0) => (
    <div key={holon.name} style={{ marginLeft: `${level * 20}px`, cursor: 'pointer' }} onClick={() => setSelectedHolon(holon)}>
      {holon.name}
      {holon.children && <div className="children">{holon.children.map(child => renderSystem(child, level + 1))}</div>}
    </div>
  );

  return <div className="canvas-view">{renderSystem(system)}</div>;
};

// DetailView Component
const DetailView = ({ selectedHolon }) => (
  <div className="detail-view">
    {!selectedHolon ? "No Selection" : (
      <>
        <h2>Holon Details</h2>
        <p>Name: {selectedHolon.name}</p>
        <p>Description: {selectedHolon.description}</p>
      </>
    )}
  </div>
);

function App() {
  const [system, setSystem] = useState(initialSystem);
  const [selectedHolon, setSelectedHolon] = useState(null);

  const handleExportSystem = useCallback(() => {
    const filename = "system_export.json";
    const jsonStr = JSON.stringify(system, null, 2);
    const element = document.createElement('a');
    element.setAttribute('href', 'data:text/json;charset=utf-8,' + encodeURIComponent(jsonStr));
    element.setAttribute('download', filename);
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  }, [system]);

  return (
    <div className="app">
      <div className="left-panel">
        <CanvasView system={system} setSelectedHolon={setSelectedHolon} />
      </div>
      <div className="right-panel">
        <DetailView selectedHolon={selectedHolon} />
        <button onClick={handleExportSystem} className="export-btn">Export System</button>
      </div>
    </div>
  );
}

export default App;
