import React, { useState } from 'react';
import './styles.css';

const Holon = ({ node, onSelect, onAdd, onRemove }) => {
  const handleAdd = (e) => {
    e.stopPropagation();
    onAdd(node);
  };

  const handleRemove = (e) => {
    e.stopPropagation();
    onRemove(node.id);
  };

  return (
    <div className={`holon holon-level-${node.level}`} onClick={() => onSelect(node)}>
      <div className="holon-details">
        <div className="name">{node.name}</div>
        <div className="buttons">
          <button className="add-button" onClick={handleAdd}>+</button>
          <button className="remove-button" onClick={handleRemove}>x</button>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const initialNodes = [{
    id: 'root',
    name: 'Information System',
    description: 'An Information System is a set of components for collecting, storing, and processing data.',
    children: [],
    level: 0,
  }];

  const [nodes, setNodes] = useState(initialNodes);
  const [selectedHolon, setSelectedHolon] = useState(null);
  const [idCounter, setIdCounter] = useState(1); // Start ID counter after the initial root node

  const selectHolon = (node) => setSelectedHolon(node);

  const addChild = (parentNode) => {
    const childName = prompt('Enter the name of the child:');
    if (!childName) return;

    const newId = `node-${idCounter}`;
    const newNode = { id: newId, name: childName, children: [], level: parentNode.level + 1 };

    const newNodes = [...nodes, newNode];
    setNodes(newNodes);
    setIdCounter(idCounter + 1);
  };

  const removeChild = (nodeId) => {
    const newNodes = nodes.filter(node => node.id !== nodeId);
    setNodes(newNodes);
  };

  return (
    <div className="app-container">
      {nodes.map(node => (
        <Holon key={node.id} node={node} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />
      ))}
      {selectedHolon && (
        <div className="holon-details-panel">
          <h2>Holon Details</h2>
          <p>Name: {selectedHolon.name}</p>
          <p>Description: {selectedHolon.description || "No description provided."}</p>
        </div>
      )}
    </div>
  );
};

export default App;
