import React, { useState } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCirclePlus, faTrash } from '@fortawesome/free-solid-svg-icons';
import './styles.css';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected, activeStep, onHover, onHoverLeave }) => {
    const handleActionClick = (e, action) => {
        e.stopPropagation();
        action();
    };

    const selected = isSelected(node);
    const handleMouseEnter = () => {
        if (activeStep === 2) onHover(node.id);
    };
    const handleMouseLeave = () => {
        if (activeStep === 2) onHoverLeave();
    };

    return (
        <TreeNode label={
            <div className={`tree-label ${selected ? 'selected' : ''}`}
                 onClick={() => onSelectNode(node)}
                 onMouseEnter={handleMouseEnter}
                 onMouseLeave={handleMouseLeave}>
                <div className="icons-container">
                    <button className="add-button" onClick={(e) => handleActionClick(e, () => onAddChild(node, true))}>
                        <FontAwesomeIcon icon={faCirclePlus} />
                    </button>
                    {activeStep === 1 && (
                        <button className="remove-button" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>
                            <FontAwesomeIcon icon={faTrash} />
                        </button>
                    )}
                </div>
                <div className="name">{node.name}</div>
            </div>
        }>
            {node.children && node.children.map(child => (
                <Holon
                    key={child.id}
                    node={child}
                    onAddChild={onAddChild}
                    onRemoveNode={onRemoveNode}
                    onSelectNode={onSelectNode}
                    isSelected={isSelected}
                    activeStep={activeStep}
                    onHover={onHover}
                    onHoverLeave={onHoverLeave}
                />
            ))}
        </TreeNode>
    );
};

const App = () => {
    const [nodes, setNodes] = useState({
        id: 'root',
        name: 'Information System',
        description: 'An information system is an organized combination of people, hardware, software, communication networks, and data resources that collects, transforms, and disseminates information in an organization.',
        children: [
            {
                id: 'Stakeholder',
                name: 'Stakeholder',
                description: 'A stakeholder refers to any individual, group, or organization that has an interest or concern in the system, its development, implementation, or outcomes. These stakeholders can be internal or external to the organization implementing the information system. Understanding and managing stakeholders is crucial for the success of an information system project.',
                children: [],
            },
            {
                id: 'Infrastructure',
                name: 'Infrastructure',
                description: 'Infrastructure in the context of information systems refers to the fundamental physical and organizational structures, facilities, and services required for the operation of an information system. This includes hardware, software, networking components, data centers, and the personnel that maintain and manage the system. Effective infrastructure supports the smooth and efficient collection, storage, processing, and dissemination of information.',
                children: [],
            }
        ],
    });
    const [selectedHolon, setSelectedHolon] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [modalStep, setModalStep] = useState(1);
    const [nodeType, setNodeType] = useState('');
    const [nodeName, setNodeName] = useState('');
    const [activeStep, setActiveStep] = useState(1);
    const [hoveredHolonId, setHoveredHolonId] = useState(null);

    const handleModalClose = () => {
        setShowModal(false);
        setModalStep(1);
        setNodeName('');
    };

    const handleModalShow = () => {
        setShowModal(true);
    };

    const onAddChild = (parentNode) => {
        setSelectedHolon(parentNode);
        handleModalShow();
    };

    const removeNode = (nodes, nodeIdToRemove) => {
        if (nodes.id === nodeIdToRemove) {
            return null;  // Node found and removed
        }
        const updatedChildren = nodes.children.map(child => removeNode(child, nodeIdToRemove)).filter(child => child !== null);
        return { ...nodes, children: updatedChildren };
    };

    const onRemoveNode = (nodeId) => {
        const newNodes = removeNode(nodes, nodeId);
        setNodes(newNodes);
        setSelectedHolon(null);
    };

    const addNode = (nodes, parentId, newNode) => {
        if (nodes.id === parentId) {
            return { ...nodes, children: [...nodes.children, newNode] };
        }
        const updatedChildren = nodes.children.map(child => addNode(child, parentId, newNode));
        return { ...nodes, children: updatedChildren };
    };

    const handleStepClick = (stepNumber) => {
        setActiveStep(stepNumber);
    };

    const handleNodeTypeSelection = (type) => {
        setNodeType(type);
        setModalStep(2);
    };

    const handleSubmit = () => {
        const newNode = {
            id: `${selectedHolon.id}-${Math.random()}`,
            name: nodeName,
            children: []
        };
        if (nodeType === 'child') {
            const newNodes = addNode(nodes, selectedHolon.id, newNode);
            setNodes(newNodes);
        }
        handleModalClose();
    };

    const handleHover = (holonId) => {
        setHoveredHolonId(holonId);
    };

    const handleHoverLeave = () => {
        setHoveredHolonId(null);
    };

    return (
        <div className="app-container">
            <div className="steps-bar">
                <div className={`step ${activeStep === 1 ? 'active' : ''}`} onClick={() => handleStepClick(1)}>
                    <div className="step-circle">1</div>
                    <div className="step-label">Structure</div>
                </div>
                <div className={`step ${activeStep === 2 ? 'active' : ''}`} onClick={() => handleStepClick(2)}>
                    <div className="step-circle">2</div>
                    <div className="step-label">Communication</div>
                </div>
            </div>
            <div className="content-area">
                <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"}>
                    <Holon
                        node={nodes}
                        onAddChild={onAddChild}
                        onRemoveNode={onRemoveNode}
                        onSelectNode={(node) => setSelectedHolon(node)}
                        isSelected={(node) => selectedHolon && node.id === selectedHolon.id}
                        activeStep={activeStep}
                        onHover={handleHover}
                        onHoverLeave={handleHoverLeave}
                    />
                </Tree>
                <div className="holon-details">
                    <h2>Holon Details</h2>
                    {selectedHolon ? (
                        <>
                            <p className='holon-details-name'>Name: {selectedHolon.name}</p>
                            <p className='holon-details-description'>Description: {selectedHolon.description || "No description available."}</p>
                            <p className='holon-details-description'>Description: {selectedHolon.description || "No description available."}</p>
                            {activeStep === 2 && (
                                <>
                                    <h3>Information Flows</h3>
                                    <p>{nodes.description}</p>
                                </>
                            )}
                        </>
                    ) : (
                        <p className='no-selection'>No Selection</p>
                    )}
                </div>
            </div>
            <Modal show={showModal} onHide={handleModalClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{modalStep === 1 ? 'Select Node Type' : 'Enter Node Name'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {modalStep === 1 ? (
                        <p style={{ textAlign: 'center' }}>
                            Select a button below to add the node you need
                        </p>
                    ) : (
                        <Form>
                            <Form.Group controlId="formNodeName">
                                <Form.Label>Enter the name for the Node</Form.Label>
                                <Form.Control type="text" placeholder="Node name" value={nodeName} onChange={(e) => setNodeName(e.target.value)} />
                            </Form.Group>
                        </Form>
                    )}
                </Modal.Body>
                <Modal.Footer>
                    {modalStep === 1 ? (
                        <>
                            <Button variant="secondary" onClick={() => handleNodeTypeSelection('child')}>Child-Node</Button>
                            <Button variant="primary" onClick={() => handleNodeTypeSelection('parent')}>Parent-Node</Button>
                        </>
                    ) : (
                        <Button variant="primary" onClick={handleSubmit}>
                            Save Changes
                        </Button>
                    )}
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default App;
