import React, { useState } from 'react';
import './styles.css'; // Import the CSS file containing styles

// Information System Component (Parent Node)
const InformationSystem = ({ onAddStakeholder, onAddInfrastructure }) => {
  const [stakeholderName, setStakeholderName] = useState('');
  const [infrastructureName, setInfrastructureName] = useState('');

  const handleAddStakeholder = () => {
    if (stakeholderName.trim() === '') {
      alert('Please enter a valid name for the Stakeholder.');
      return;
    }

    onAddStakeholder(stakeholderName);
    setStakeholderName('');
  };

  const handleAddInfrastructure = () => {
    if (infrastructureName.trim() === '') {
      alert('Please enter a valid name for the Infrastructure.');
      return;
    }

    onAddInfrastructure(infrastructureName);
    setInfrastructureName('');
  };

  return (
    <div className="holon information-system">
      <div className="name">Information System</div>
      <div className="children">
        <div className="child-container">
          <input
            type="text"
            placeholder="Enter Stakeholder Name"
            value={stakeholderName}
            onChange={(e) => setStakeholderName(e.target.value)}
          />
          <button className="add-button" onClick={handleAddStakeholder}>+</button>
        </div>
        <div className="child-container">
          <input
            type="text"
            placeholder="Enter Infrastructure Name"
            value={infrastructureName}
            onChange={(e) => setInfrastructureName(e.target.value)}
          />
          <button className="add-button" onClick={handleAddInfrastructure}>+</button>
        </div>
      </div>
    </div>
  );
};

// Stakeholder Component (Child Node)
const Stakeholder = ({ name, onRemove }) => {
  return (
    <div className="holon stakeholder">
      <div className="name">{name}</div>
      <button className="remove-button" onClick={onRemove}>x</button>
    </div>
  );
};
