import React, { useState } from 'react';
import './styles.css';

const Holon = ({ id, name, onSelectNode, isSelected, setSelectedHolonId, selectedHolonId, onHoveredHolon }) => {
    const [isHovered, setIsHovered] = useState(false);

    const handleNodeClick = () => {
        setSelectedHolonId(id);
    };

    const handleMouseEnter = () => {
        setIsHovered(true);
        onHoveredHolon(id);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
        onHoveredHolon(null);
    };

    const selected = isSelected(id);
    const isOrigin = selectedHolonId === id;

    return (
        <div
            className={`holon ${selected ? 'selected' : ''} ${isOrigin ? 'origin' : ''} ${isHovered ? 'hovered' : ''}`}
            onClick={handleNodeClick}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
        >
            {name}
        </div>
    );
};

const Connection = ({ originId, targetId }) => {
    return (
        <div className="connection" data-origin={originId} data-target={targetId}></div>
    );
};

const CommunicationPage = () => {
    const initialNodes = [
        { id: 'Stakeholder', name: 'Stakeholder' },
        { id: 'Infrastructure', name: 'Infrastructure' }
    ];

    const [selectedHolonId, setSelectedHolonId] = useState(null);
    const [hoveredHolonId, setHoveredHolonId] = useState(null);

    const handleSelectNode = (id) => {
        setSelectedHolonId(id);
    };

    const isSelected = (id) => {
        return selectedHolonId === id;
    };

    const onHoveredHolon = (id) => {
        setHoveredHolonId(id);
    };

    return (
        <div className="app-container">
            <div className="content-area">
                <div className="holons">
                    {initialNodes.map(node => (
                        <Holon
                            key={node.id}
                            id={node.id}
                            name={node.name}
                            onSelectNode={handleSelectNode}
                            isSelected={isSelected}
                            setSelectedHolonId={setSelectedHolonId}
                            selectedHolonId={selectedHolonId}
                            onHoveredHolon={onHoveredHolon}
                        />
                    ))}
                </div>
                <div className="connections">
                    {initialNodes.map(node => (
                        <Connection
                            key={`${node.id}-connection`}
                            originId={node.id}
                            targetId={hoveredHolonId === node.id ? hoveredHolonId : null}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default CommunicationPage;
