import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './styles.css';

// Dummy data for demonstration
const initialNodes = {
  id: 'root',
  name: 'Information System',
  children: [
    {
      id: '1',
      name: 'Stakeholder',
      description: 'Description for Stakeholder',
      children: [],
    },
    {
      id: '2',
      name: 'Infrastructure',
      description: 'Description for Infrastructure',
      children: [],
    },
  ],
};

const Holon = ({ node, onAddChild, onRemoveNode }) => {
  return (
    <div>
      <div>{node.name}</div>
      <Link to={`/communication/${node.id}`}>Details</Link>
      {/* Implement onAddChild and onRemoveNode as needed */}
    </div>
  );
};

const StructurePage = ({ nodes }) => {
  return (
    <div>
      {nodes.children.map((node) => (
        <Holon key={node.id} node={node} />
      ))}
    </div>
  );
};

const CommunicationPage = () => {
  // Assuming useParams to extract holonId and find the relevant holon details
  return <div>Details Page</div>;
};

const App = () => {
  const [nodes] = useState(initialNodes);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<StructurePage nodes={nodes} />} />
        <Route path="/communication/:holonId" element={<CommunicationPage />} />
      </Routes>
    </Router>
  );
};

export default App;
