import React, { useState } from 'react';
import './App.css';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
  // Prevents event bubbling to avoid selecting a node when clicking on add/remove buttons
  const handleActionClick = (e, action) => {
    e.stopPropagation();
    action();
  };

  return (
    <div className={`holon ${isSelected ? 'selected' : ''}`} onClick={() => onSelectNode(node)}>
      <div className="holon-content">
        <span>{node.name}</span>
        <div className="buttons">
          <button onClick={(e) => handleActionClick(e, () => onAddChild(node))}>+</button>
          {node.id !== 'root' && <button onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>x</button>}
        </div>
      </div>
      {node.children && node.children.map(child => (
        <Holon 
          key={child.id} 
          node={child} 
          onAddChild={onAddChild} 
          onRemoveNode={onRemoveNode} 
          onSelectNode={onSelectNode}
          isSelected={isSelected && isSelected.id === child.id}
        />
      ))}
    </div>
  );
};

const App = () => {
  const initialNodes = {
    id: 'root',
    name: 'Information System',
    children: [],
  };

  const [nodes, setNodes] = useState(initialNodes);
  const [selectedHolon, setSelectedHolon] = useState(null);

  const onAddChild = (parentNode) => {
    const childName = prompt('Enter the name of the child:');
    if (!childName) return;

    const newNode = {
      id: `${parentNode.id}-${Math.random()}`,
      name: childName,
      children: [],
    };

    const addNode = (nodes, parentId) => {
      if (nodes.id === parentId) {
        nodes.children = [...nodes.children, newNode];
        return;
      }

      nodes.children.forEach(child => addNode(child, parentId));
    };

    const newNodes = {...nodes};
    addNode(newNodes, parentNode.id);
    setNodes(newNodes);
  };

  const onRemoveNode = (nodeId) => {
    const removeNode = (nodes, nodeId) => {
      nodes.children = nodes.children.filter(child => child.id !== nodeId);
      nodes.children.forEach(child => removeNode(child, nodeId));
    };

    const newNodes = {...nodes};
    removeNode(newNodes, nodeId);
    setNodes(newNodes);
  };

  return (
    <div className="app-container">
      <Holon
        node={nodes}
        onAddChild={onAddChild}
        onRemoveNode={onRemoveNode}
        onSelectNode={setSelectedHolon}
        isSelected={selectedHolon}
      />
      {selectedHolon && (
        <div className="selected-holon-details">
          <h2>Holon Details</h2>
          <p>Name: {selectedHolon.name}</p>
          <p>ID: {selectedHolon.id}</p>
        </div>
      )}
    </div>
  );
};

export default App;
