import React, { useState } from 'react';
import { Tree } from 'react-organizational-chart';
import './styles.css';


const Alert = ({ message }) => {
    return (
        <div className="alert">
            {message}
        </div>
    );
};

const CommunicationPage = () => {
    const initialNodes = {
        id: 'root',
        name: 'Information System',
        children: [
            {
                id: 'Stakeholder',
                name: 'Stakeholder',
                children: [],
            },
            {
                id: 'Infrastructure',
                name: 'Infrastructure',
                children: [],
            }
        ],
    };

    const [selectedHolonIds, setSelectedHolonIds] = useState([]);
    const [hoveredHolonId, setHoveredHolonId] = useState(null);
    const [showAddSign, setShowAddSign] = useState(true);
    const [showRegulationLine, setShowRegulationLine] = useState(false);
    const [showAddMessage, setShowAddMessage] = useState(false); // Define showAddMessage state

    const toggleAddMessage = (value) => {
        setShowAddMessage(value); // Toggle the message
    };

    const handleSelectNode = (node) => {
        setSelectedHolonIds([node.id]);
        setShowAddSign(true); // Show the add signs on all Holons
    };

    const isSelected = (node) => selectedHolonIds.includes(node.id);

    return (
        <div className="app-container">
            <div className="content-area">
                <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"} lineStyle={"dashed"}>
                    <Holon
                        node={initialNodes}
                        onSelectNode={handleSelectNode}
                        isSelected={isSelected}
                        setSelectedHolonIds={setSelectedHolonIds}
                        hoveredHolonId={hoveredHolonId}
                        setHoveredHolonId={setHoveredHolonId}
                        showAddSign={showAddSign}
                        setShowAddSign={setShowAddSign}
                        showRegulationLine={showRegulationLine}
                        toggleAddMessage={toggleAddMessage} // Pass down function to toggle the message
                    />
                </Tree>
            </div>
            {showAddMessage && <Alert message="Click another holon to add an information flow." />}
        </div>
    );
};

export default CommunicationPage;
