import React, { useState } from 'react';
import './styles.css';

const Holon = ({ name, onSelect, onAdd, onRemove }) => {
  return (
    <div className="holon" onClick={() => onSelect(name)}>
      <div className="name">{name}</div>
      <button className="add-button" onClick={(e) => onAdd(e, name)}>+</button>
      <button className="remove-button" onClick={(e) => onRemove(e, name)}>x</button>
    </div>
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    {
      id: 'root',
      name: 'Information System',
      description: 'An Information System is a set of components for collecting, storing, and processing data.',
      children: [],
    }
  ]);
  const [selectedHolon, setSelectedHolon] = useState(null);

  const selectHolon = (name) => {
    const holon = findNode(nodes, name);
    setSelectedHolon(holon);
  };

  const addChild = (e, parentName) => {
    e.stopPropagation();
    const childName = prompt("Enter the name for the new node:");
    if (!childName) return; // Cancelled prompt or empty input

    setNodes((prevNodes) => {
      const newNodes = [...prevNodes];
      const addNodeRecursively = (nodes) => {
        nodes.forEach((node) => {
          if (node.name === parentName) {
            const newChild = { id: childName.toLowerCase(), name: childName, description: '', children: [] };
            node.children = [...node.children, newChild];
          } else if (node.children.length) {
            addNodeRecursively(node.children);
          }
        });
      };
      addNodeRecursively(newNodes);
      return newNodes;
    });
  };

  const removeChild = (e, nodeName) => {
    e.stopPropagation();
    setNodes((prevNodes) => {
      const newNodes = removeNodeRecursively([...prevNodes], nodeName);
      return newNodes;
    });
  };

  const findNode = (nodes, name) => {
    for (const node of nodes) {
      if (node.name === name) return node;
      if (node.children.length) {
        const found = findNode(node.children, name);
        if (found) return found;
      }
    }
    return null;
  };

  const removeNodeRecursively = (nodes, name) => {
    return nodes.reduce((acc, node) => {
      if (node.name === name) return acc; // Skip the node to be removed
      if (node.children.length) node.children = removeNodeRecursively(node.children, name);
      acc.push(node); // Keep the node if not matched
      return acc;
    }, []);
  };

  return (
    <div className="app-container">
      <div className="holons-container">
        {nodes.map((node, index) => (
          <div key={index}>
            <Holon key={node.id} name={node.name} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />
            {node.children.length > 0 && (
              <div className="children">
                {node.children.map((child) => (
                  <Holon key={child.id} name={child.name} onSelect={selectHolon} onAdd={addChild} onRemove={removeChild} />
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
      <div className="holon-details">
        {selectedHolon ? (
          <>
            <h2>Holon Details</h2>
            <p>Name: {selectedHolon.name}</p>
            <p>Description: {selectedHolon.description}</p>
          </>
        ) : (
          <p>Select a holon to see details.</p>
        )}
      </div>
    </div>
  );
};

export default App;
