import React, { useState, useEffect } from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import './styles.css';

const fetchNodes = async () => {
  const response = await fetch('/nodes');
  return response.json();
};

const addNode = async (node) => {
  const response = await fetch('/nodes', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(node)
  });
  return response.json();
};

const removeNode = async (id) => {
  await fetch(`/nodes/${id}`, {
    method: 'DELETE'
  });
};

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected, addNewRoot }) => {
    const handleActionClick = (e, action) => {
        e.stopPropagation();
        action();
    };

    const selected = isSelected(node);

    return (
        <TreeNode label={
            <div className={`tree-label ${selected ? 'selected' : ''}`} onClick={() => onSelectNode(node)}>
                <div className="name">{node.name}</div>
                <div className="buttons">
                    <button className="add-button" onClick={(e) => handleActionClick(e, () => onAddChild(node, true))}>+</button>
                    <button className="remove-button" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>x</button>
                </div>
            </div>
        }>
            {node.children && node.children.map(child => (
                <Holon
                    key={child.id}
                    node={child}
                    onAddChild={onAddChild}
                    onRemoveNode={onRemoveNode}
                    onSelectNode={onSelectNode}
                    isSelected={isSelected}
                    addNewRoot={addNewRoot}
                />
            ))}
        </TreeNode>
    );
};

const App = () => {
    const [nodes, setNodes] = useState(null);
    const [selectedHolon, setSelectedHolon] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [modalStep, setModalStep] = useState(1);
    const [nodeType, setNodeType] = useState('');
    const [nodeName, setNodeName] = useState('');

    useEffect(() => {
      fetchNodes().then(setNodes);
    }, []);

    const handleModalClose = () => {
        setShowModal(false);
        setModalStep(1);
        setNodeName('');
    };

    const handleModalShow = () => {
        setShowModal(true);
    };

    const onAddChild = async (parentNode) => {
        setSelectedHolon(parentNode);
        handleModalShow();
    };

    const onRemoveNode = async (nodeId) => {
        await removeNode(nodeId);
        await fetchNodes().then(setNodes);
        setSelectedHolon(null);
    };

    const addNewRoot = () => {
        setSelectedHolon({ id: 'root', name: '', children: [nodes] });
        setNodeType('parent');
        handleModalShow();
    };

    const handleNodeTypeSelection = (type) => {
        setNodeType(type);
        setModalStep(2);
    };

    const handleSubmit = async () => {
        const newNode = {
            name: nodeName,
            parentId: nodeType === 'child' ? selectedHolon.id : null,
            children: []
        };
        await addNode(newNode);
        await fetchNodes().then(setNodes);
        handleModalClose();
    };

    if (!nodes) return <div>Loading...</div>;

    return (
        <div className="app-container">
            {/* Rest of your component remains the same */}
        </div>
    );
};

export default App;
