import React from 'react';
import { Tree, TreeNode } from 'react-organizational-chart';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';
import { Button, Modal } from 'react-bootstrap'; // Import Button and Modal from react-bootstrap
import './styles.css';

const Holon = ({ node, onAddChild, onRemoveNode, onSelectNode, isSelected }) => {
    const handleActionClick = (e, action) => {
        e.stopPropagation();
        action();
    };

    const selected = isSelected(node);

    return (
        <TreeNode label={
            <div className={`tree-label ${selected ? 'selected' : ''}`} onClick={() => onSelectNode(node)}>
                <div className="name">{node.name}</div>
                <div className="buttons">
                    <button className="add-button" onClick={(e) => handleActionClick(e, () => onAddChild(node, true))}>
                        <FontAwesomeIcon icon={faPlus} />
                    </button>
                    <button className="remove-button" onClick={(e) => handleActionClick(e, () => onRemoveNode(node.id))}>
                        <FontAwesomeIcon icon={faTrash} />
                    </button>
                </div>
            </div>
        }>
            {node.children && node.children.map(child => (
                <Holon
                    key={child.id}
                    node={child}
                    onAddChild={onAddChild}
                    onRemoveNode={onRemoveNode}
                    onSelectNode={onSelectNode}
                    isSelected={isSelected}
                />
            ))}
        </TreeNode>
    );
};

const StructurePage = ({ nodes, onAddChild, onRemoveNode, onSelectNode, isSelected, addNewRoot }) => {
    // State to manage modal visibility
    const [showModal, setShowModal] = React.useState(false);

    // Function to handle modal closing
    const handleClose = () => setShowModal(false);

    return (
        <div className="page">
            <div className="tree-container">
                <Tree lineWidth={"2px"} lineColor={"blue"} lineBorderRadius={"5px"} lineStyle={"dashed"}>
                    <Holon
                        node={nodes}
                        onAddChild={onAddChild}
                        onRemoveNode={onRemoveNode}
                        onSelectNode={onSelectNode}
                        isSelected={(node) => isSelected && node.id === isSelected.id}
                    />
                </Tree>
            </div>

            {/* Modal component */}
            <Modal show={showModal} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal title</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <p>Modal body text goes here.</p>
                </Modal.Body>
                <Modal.Footer>
                    {/* Save button with conditional styling */}
                    <Button variant="primary">Save changes</Button>

                    {/* Close button */}
                    <Button variant="secondary" onClick={handleClose}>Close</Button>
                    
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default StructurePage;
