import React, { useState } from 'react';
import './styles.css'; // Import the CSS file containing styles

// Information System Component (Parent Node)
const Stakeholder = () => {
  return (
    <div className="stakeholder-box">
      <h2>Stakeholder</h2>
    </div>
  );
};

// Infrastructure Component
const Infrastructure = () => {
  return (
    <div className="infrastructure-box">
      <h2>Infrastructure</h2>
    </div>
  );
};

// Information System Component (Parent Node)
const InformationSystem = () => {
  return (
    <div className="information-system-box">
      <h2>Information System</h2>
      <div className="child-container">
        <Stakeholder />
        <Infrastructure />
      </div>
    </div>
  );
};
// App Component
const App = () => {
  const [stakeholderList, setStakeholderList] = useState([]);
  const [infrastructureList, setInfrastructureList] = useState([]);

  const addStakeholder = (name) => {
    setStakeholderList([...stakeholderList, name]);
  };

  const addInfrastructure = (name) => {
    setInfrastructureList([...infrastructureList, name]);
  };

  const removeStakeholder = (index) => {
    const updatedList = [...stakeholderList];
    updatedList.splice(index, 1);
    setStakeholderList(updatedList);
  };

  const removeInfrastructure = (index) => {
    const updatedList = [...infrastructureList];
    updatedList.splice(index, 1);
    setInfrastructureList(updatedList);
  };

  return (
    <div className="container">
      <InformationSystem
        onAddStakeholder={addStakeholder}
        onAddInfrastructure={addInfrastructure}
      />
      <div className="stakeholder-box">
        <h2>Stakeholders</h2>
        <div className="stakeholder-container">
          {stakeholderList.map((stakeholder, index) => (
            <Stakeholder
              key={index}
              name={stakeholder}
              onRemove={() => removeStakeholder(index)}
            />
          ))}
        </div>
      </div>
      <div className="infrastructure-box">
        <h2>Infrastructure</h2>
        <div className="infrastructure-container">
          {infrastructureList.map((infrastructure, index) => (
            <Infrastructure
              key={index}
              name={infrastructure}
              onRemove={() => removeInfrastructure(index)}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
